                                function getImageWidth(url, callback) {
                                    var img = new Image();
                                    var imgwidth, imgheigth, showMaxw, showMinw, showMaxh, showMinh, showNoww, showNowh, imgnarrow;
                                    img.src = url;
                                    imgwidth = img.width; //图片原始宽度
                                    imgheight = img.height; //图片原始高度
                                    showMaxw = 450;
                                    showMinw = 382;
                                    showMaxh = 400;
                                    showMinh = 256;
                                    if (imgwidth >= imgheight) { //横向图
                                        if (imgwidth >= showMaxw) {//如果图片宽度超出450
                                            imgnarrow = 1;
                                            var maxh = (showMaxw * imgheight) / imgwidth; //通过宽度计算高度等比缩放
                                            showNoww = showMaxw;
                                            if (maxh >= showMinh) {  //缩放后图片高度超过320
                                                showNowh = maxh;
                                            } else { //缩放后图片高度小于320
                                                if (imgheight >= showMinh) { //判断原图高度超过320
                                                    showNowh = maxh
                                                }
                                                else {//不超过320
                                                    //不做等比例缩放
                                                    imgnarrow = 0;
                                                    showNoww = imgwidth;
                                                    showNowh = imgheight;
                                                }
                                            }
                                        } else { //如果图片宽度不超出450 直接使用原图高宽
                                            imgnarrow = 0;
                                            showNoww = imgwidth;
                                            showNowh = imgheight;
                                            if (imgwidth >= showMinw) {
                                                imgnarrow = 1;
                                            }


                                        }
                                    } else { //竖向图
