    con+="rc='http://i5.vanclimg.com/cms/20161011/yuan199x.png' class='catalog_half_pointxxx'>";

    $(".shirt-listxxx ul li").append(con);

    $(".shirts-product-list").each(function (j, k) {
        $(k).find("li").each(function (i, o) {

            var pri = 0;

            pri = parseFloat($(o).find(".sprice").text().substr(1));
            if (isNaN(pri)) {
                pri = parseFloat($.trim($(o).find(".price").first().text()).substr(1));
            }
            var priNew = (pri) / 2;
            if (pri % 2 == 0) {
                priNew = priNew.toFixed(0);
            } else {
                priNew = priNew.toFixed(1);
            }
            var txt = "<span class='price recharge'>充值购买相当于￥" + priNew + "</span>";
            $(o).find(".price").after(txt);
        })

    });

    /*焦点图初始化脚本*/
    $("#rslides2016").responsiveSlides({ auto: true, pager: true, speed: 500, pause: true, nav: true });

    /*导航滚动事件*/
    var arrTop = [];
    var navAs = $("#nautilus_subnav li a");
    navAs.each(function(i, v) {
        if (v.hash) {
            arrTop[i] = $(v.hash).offset().top - 500;
        }
    });

    function highlightAlink(sTop) {
        if (sTop < arrTop[1] && sTop>= 1000) {
            navAs[0].style.color = '#ca0000';
        } else {
            navAs[0].style.color = '#676767';
        }
        if (sTop < arrTop[2] && sTop>= arrTop[1]) {
            navAs[1].style.color = '#ca0000';
        } else {
            navAs[1].style.color = '#676767';
        }
        if (sTop < arrTop[3] && sTop>= arrTop[2]) {
            navAs[2].style.color = '#ca0000';
        } else {
            navAs[2].style.color = '#676767';
        }
        if (sTop < arrTop[4] && sTop>= arrTop[3]) {
            navAs[3].style.color = '#ca0000';
        } else {
            navAs[3].style.color = '#676767';
        }
        if (sTop < arrTop[5] && sTop>= arrTop[4]) {
            navAs[4].style.color = '#ca0000';
        } else {
            navAs[4].style.color = '#676767';
        }
        if (sTop < arrTop[6] && sTop>= arrTop[5]) {
            navAs[5].style.color = '#ca0000';
        } else {
            navAs[5].style.color = '#676767';
        }
        if (sTop < arrTop[7] && sTop>= arrTop[6]) {
            navAs[6].style.color = '#ca0000';
        } else {
            navAs[6].style.color = '#676767';
        }
        if (sTop>= arrTop[7]) {
            navAs[7].style.color = '#ca0000';
        } else {
            navAs[7].style.color = '#676767';
        }

    }


    $(document).scroll(function() {
        var nav = $("#nautilus_subnav");
        var scrollTop = $(document).scrollTop();
        if (scrollTop>= 200) {
            nav.removeClass('nautilus_subnav_two').addClass('nautilus_subnav_two_1');
            highlightAlink(scrollTop);

        } else {
            navAs.css('color', '#676767');
            nav.addClass('nautilus_subnav_two').removeClass('nautilus_subnav_two_1');
        }
    });

</script>
</body>
</html>
